/*1*/ SELECT LAST_NAME, SALARY FROM EMPLOYEES WHERE SALARY > 12000;
/*2*/ SELECT LAST_NAME ||', '|| DEPARTMENT_ID "Emp 176 LastName and DeptNo" FROM EMPLOYEES WHERE EMPLOYEE_ID = 176;
/*3*/ SELECT LAST_NAME, SALARY FROM EMPLOYEES WHERE SALARY < 5000 OR SALARY > 12000;
/*4*/ SELECT LAST_NAME ||', '|| JOB_ID ||', '|| HIRE_DATE "Last Name, Job ID, Hire Date" FROM EMPLOYEES WHERE LAST_NAME = 'Matos' OR LAST_NAME = 'Taylor' ORDER BY HIRE_DATE;
/*5*/ SELECT LAST_NAME, DEPARTMENT_ID FROM EMPLOYEES WHERE DEPARTMENT_ID = 20 OR DEPARTMENT_ID = 50 ORDER BY LAST_NAME;
/*6*/ SELECT LAST_NAME AS "Employee", SALARY AS "Monthly Salary" FROM EMPLOYEES WHERE (SALARY >= 5000 AND SALARY <= 12000) AND (DEPARTMENT_ID = 20 OR DEPARTMENT_ID = 50);
/*7*/ SELECT LAST_NAME ||' - '|| HIRE_DATE "Employee Name - Hire Date" FROM EMPLOYEES WHERE EXTRACT (YEAR FROM HIRE_DATE) = 1994;
/*8*/ SELECT E.LAST_NAME ||' - '|| J.JOB_TITLE "Employees with no manager" FROM EMPLOYEES E, JOBS J WHERE E.JOB_ID = J.JOB_ID AND E.MANAGER_ID IS NULL;
/*9*/ SELECT LAST_NAME, SALARY, COMMISSION_PCT FROM EMPLOYEES WHERE COMMISSION_PCT IS NOT NULL ORDER BY 2, 3 DESC;
/*10*/ SELECT LAST_NAME ||' - '|| SALARY "Salary more than input" FROM EMPLOYEES WHERE SALARY > &SALARY_VALUE;
/*11*/ SELECT MANAGER_ID, EMPLOYEE_ID, LAST_NAME, SALARY, DEPARTMENT_ID FROM EMPLOYEES WHERE MANAGER_ID = &MANAGER_ID ORDER BY &SORTED_BY;
/*12*/ SELECT LAST_NAME FROM EMPLOYEES WHERE LAST_NAME LIKE '__a%';
/*13*/ SELECT LAST_NAME FROM EMPLOYEES WHERE (LAST_NAME LIKE '%a%e%') OR (LAST_NAME LIKE '%e%a%');
/*14*/ SELECT E.LAST_NAME, J.JOB_TITLE, E.SALARY FROM EMPLOYEES E, JOBS J WHERE (E.JOB_ID = J.JOB_ID AND J.JOB_TITLE = 'Sales Representative' OR J.JOB_TITLE = 'Stock Clerk') AND NOT (E.SALARY = 2500 OR E.SALARY = 3500 OR E.SALARY = 7000);
